import React from 'react';
import { useState } from 'react';
import { data } from './data';


const UseStateCards = () => {
    const [people, setPeople] = useState(data);

    const clearAll = () => {

        const Hang = document.getElementsByClassName("btn");
        const newBday = document.getElementsByClassName("bday");

        Hang.onClick = function(){setPeople([])};

    }


    return (
        <>
            {people.map((person) => {
                const { id, image, name, age } = person
                return (

                    <div key={id} className="item">
                        <div className="image">
                            <img src={image} alt="" />
                        </div>
                        <div className="words">
                            <div className="name"><h4>{name}</h4></div>
                            <div className="age"><h5>{age}</h5></div>   
                        </div>
                    </div>
                );
            })}

            <button className="btn" onClick={()=> clearAll(className)}>Clear All</button>
        </>
    );
};

export default UseStateCards;
